import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [order, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("http://localhost:3001/orders");
        setOrders(res.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchOrders();
  }, []);

  const handleUpdate = async (order) => {
    const TaskName = order.Task_Name;
    console.log(TaskName);
    try {
      const res = await axios.post("http://localhost:3001/status", {
        TaskName,
      });
      console.log(res.data.message);
      alert("Status Updated successfully");
      window.location.reload();
    } catch (error) {
      console.error(error);
      alert("Status already updated");
    }
  };

  return (
    <div>
      <div className="main-content">
        <h2 className="main-content_h2">Orders</h2>
        <table>
          <thead>
            <tr>
              <th>Task Name</th>

              <th>Status</th>
              <th>priority</th>
              <th>Change Status</th>
            </tr>
          </thead>
          <tbody>
            {order.map((order) => (
              <tr key={order.idtasks}>
                <td>{order.Task_Name}</td>

                <td>{order.status}</td>
                <td>{order.Task_priority}</td>
                <td>
                  <button
                    className="main-content_button"
                    onClick={() => handleUpdate(order)}
                  >
                    Done
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
