import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [order, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("http://localhost:3001/orders");
        setOrders(res.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchOrders();
  }, []);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    const hours = String(date.getHours()).padStart(2, "0");
    const minutes = String(date.getMinutes()).padStart(2, "0");
    const seconds = String(date.getSeconds()).padStart(2, "0");
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
  };

  const handleUpdate = async (order) => {
    const TaskName = formatDate(order.TaskName);
    try {
      const res = await axios.post("http://localhost:3001/status", {
        productId,
      });
      console.log(res.data.message);
      alert("Status Updated successfully");
      window.location.reload();
    } catch (error) {
      console.error(error);
      alert("Status already added");
    }
  };

  return (
    <div>
      <div className="main-content">
        <h2 className="main-content_h2">Orders</h2>
        <table>
          <thead>
            <tr>
              <th>Task Name</th>

              <th>Status</th>
              <th>priority</th>
              <th>Change Status</th>
            </tr>
          </thead>
          <tbody>
            {order.map((order) => (
              <tr key={order.idtasks}>
                <td>{order.Task_Name}</td>

                <td>{order.status}</td>
                <td>{order.Task_priority}</td>
                <td>
                  <button
                    className="main-content_button"
                    onClick={() => handleUpdate(order)}
                  >
                    Done
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
