import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [order, setOrders] = useState([]);
  const [name, setName] = useState("");
  const [priority, setPriority] = useState("");

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("http://localhost:3001/orders");
        setOrders(res.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchOrders();
  }, []);

  const handleUpdate = async (order) => {
    const TaskName = order.Task_Name;
    console.log(TaskName);
    try {
      const res = await axios.post("http://localhost:3001/status", {
        TaskName,
      });
      console.log(res.data.message);
      alert("Status Updated successfully");
      window.location.reload();
    } catch (error) {
      console.error(error);
      alert("Status already updated");
    }
  };

  const handleRemove = async (order) => {
    const TaskName = order.Task_Name;
    console.log(TaskName);
    try {
      const res = await axios.post("http://localhost:3001/remove", {
        TaskName,
      });
      console.log(res.data.message);
      alert("Status Deleted successfully");
      // window.location.reload();
    } catch (error) {
      console.error(error);
      alert("Status already Deleted");
    }
  };

  const handleadd = async (order) => {
    const TaskName = order.Task_Name;
    const priority = order.Task_priority;
    console.log(TaskName);
    try {
      const res = await axios.post("http://localhost:3001/add", {
        TaskName,
        priority,
      });
      console.log(res.data.message);
      alert("Task Added successfully");
      window.location.reload();
    } catch (error) {
      console.error(error);
      alert("task already Added");
    }
  };

  return (
    <div>
      <div className="main-content">
        <h2 className="main-content_h2">Tasks</h2>
        <form onSubmit={handleadd}>
          <input
            type="text"
            placeholder="Task name"
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="number"
            placeholder="prority level"
            min={1}
            max={5}
            onChange={(e) => setPriority(e.target.value)}
          />
          <button type="submit">Add</button>
        </form>

        <table>
          <thead>
            <tr>
              <th>Task Name</th>

              <th>Status</th>
              <th>priority</th>
              <th>Change Status</th>
              <th>Remove</th>
            </tr>
          </thead>
          <tbody>
            {order.map((order) => (
              <tr key={order.idtasks}>
                <td>{order.Task_Name}</td>

                <td>{order.status}</td>
                <td>{order.Task_priority}</td>
                <td>
                  <button
                    className="main-content_button"
                    onClick={() => handleUpdate(order)}
                  >
                    Done
                  </button>
                </td>
                <td>
                  <button
                    className="main-content_button"
                    onClick={() => handleRemove(order)}
                  >
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
